import {
  EventEmitter
} from 'node:events'

import {
  readFileSync
} from 'node:fs'

const TREE = JSON.parse(readFileSync('./tree.json'))

class Gulp extends EventEmitter {
  task (name, func) {
    console.log('task', name, func)
  }

  tree () {
    return TREE
  }

  series () {
    return function task (func) {
      console.log('series', func)
    }
  }

  parallel () {
    return function task (func) {
      console.log('parallel', func)
    }
  }
}


export default new Gulp()
